package week1.day2;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 7;
		//for-> ctrl+space => end option
		for (int i = 0; i<num; i++)
		{
			System.out.println("Number i: "+ i);
		}
		
		for (int j = num; j>0; j--)
		{
			System.out.println("Number j: "+ j);
		}
	}

}
